import React from "react"
import ReactDom from "react-dom"
import './index.css';
import {Explorer, sites} from "adventure-component-library"
import axios from 'axios';
var userAddress;
const ethereum = window.ethereum;


class App extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            account: null
        }
    }

    // Next two functions will get users MetaMask public key
    componentDidMount() {
        this.connectMM()
    }

    async connectMM() {
        this.setState({account: await ethereum.enable()}, () => {
            console.log(this.state.account);
            userAddress = this.state.account[0];
        });
    }

    // Send tokens
    sendHello() {
        let ua = userAddress;
    }


    render(){
        return (
            <div className = "container">
                <div className = "titles">
                    <h1>Hi, There!</h1>
                    <h2> Request a Hello Token to wave back!</h2>
                </div>
                <div className = "waveGif">
                    <img className = "wave" src = "https://media0.giphy.com/media/ZCwUKGiF877e01bDz3/giphy.gif?cid=ecf05e47c34d3c24a46dd084d8d954fabcb688123d61308b&rid=giphy.gif"/>
                </div>                
                <div className="metaMaskButton">
                    <button className="button" onClick={this.sendHello()}>
                        Give me a Hello Token
                    </button>
                </div>

                <div><h1>{userAddress}</h1></div>

                <div className = "advLogo">
                    <img className = "logo" src = "http://13.57.47.139/adventure-logo.png"/>
                </div>
            </div>
        );
    } 
}


ReactDom.render(<App />,document.getElementById("root"));
