import ReactDom from "react-dom"
import './index.css';
import {Explorer, sites} from "adventure-component-library"
import axios from 'axios';
var buttonName = "Connect With Metamask";

function GetImageWave(props) {
    return (
        <img className = "wave" src = "https://media0.giphy.com/media/ZCwUKGiF877e01bDz3/giphy.gif?cid=ecf05e47c34d3c24a46dd084d8d954fabcb688123d61308b&rid=giphy.gif"/>
    );
}

function GetImageLogo(props) {
    return (
        <img className = "logo" src = "http://13.57.47.139/adventure-logo.png"/>
    );
}

function MMButton(props) {
    return (
        <button className="button" onClick={this.connectMM}>
          {props.value}
          Connect With MetaMask
        </button>
      );
}

